const search_form=document.querySelector(".search-form form"),search_input=search_form.querySelector("input");search_form.addEventListener("submit",e=>{let t=e.target.closest(".search-form");""===t.querySelector("input").value&&(e.preventDefault(),t.classList.add("search-form_focus"),t.querySelector("input").focus())}),search_input.addEventListener("blur",t=>{if(""===t.target.value){let e=t.target.closest(".search-form");e.classList.remove("search-form_focus")}});const mobile_button=document.querySelector(".header__mobile-button"),trigger_click="click";mobile_button.addEventListener(trigger_click,function(e){e.target.closest(".header").classList.toggle("header_open")});