const search_form=document.querySelector(".search-form form"),search_input=search_form.querySelector("input");search_form.addEventListener("submit",e=>{let r=e.target.closest(".search-form");r.classList.contains("search-form_focus")||(e.preventDefault(),r.classList.add("search-form_focus"),r.querySelector("input").focus())}),search_input.addEventListener("blur",r=>{if(""===r.target.value){let e=r.target.closest(".search-form");e.classList.remove("search-form_focus")}});